package com.imancha.test;

public class assignValue {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean b = true;
		System.out.println("Value b = "+b);
		
		char c = 'C';
		System.out.println("Value c = "+c);
		
		byte t = 10;
		System.out.println("Value t = "+t);
		
		short s = 20;
		System.out.println("Value s = "+s);
		
		int i = 30;
		System.out.println("Value i = "+i);
		
		long l = 40L;
		System.out.println("Value l = "+l);
		
		float f = 3.14F;
		System.out.println("Value f = "+f);
		
		double d = 3.14;
		System.out.println("Value d = "+d);
	}

}
