package com.imancha.test;

public class divideValue {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		float a = 15;
		float b = 6;
		float result = a / b;
		float remain = a % b;
		
		System.out.println(a+" divided by "+b);
		System.out.println("\nResult\tRemain");
		System.out.println(result+"\t"+remain);
	}

}
