package com.imancha.test;

public class hexaToDecimal {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x = 0x0001;
		int y = 0x7fffffff;
		int z = 0xDeadCafe;
		
		System.out.println("Hexadecimal 0x0001     = "+x+" Decimal");
		System.out.println("Hexadecimal 0x7fffffff = "+y+" Decimal");
		System.out.println("Hexadecimal 0xDeadCafe = "+z+" Decimal");
	}

}
