package com.imancha.test;

public class incrementDecrement {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x = 8;
		int y = 13;
		
		System.out.println("x   = "+x);
		System.out.println("y   = "+y);
		System.out.println("++x = "+(++x));
		System.out.println("y++ = "+(y++));
		System.out.println("x-- = "+(x--));
		System.out.println("--y = "+(--y));
	}

}
